INSERT INTO clothes(name, gender, brand, description, prize, type, image) values('Koszulka adidas Polo M', 'Męskie', 'Adidas', 'Oficjalny model koszulki kadry polskich siatkarzy z Mistrzostw Europy w Turcji z 2009 r. Podczas tego turnieju polscy siatkarze zdobyli złoty medal ME, z kolei Piotr Gruszka został uznany MVP całego turnieju. Ekipa dowodzona wówczas przez Daniela Castellaniego bez porażki przebrnęła przez cały turniej zwyciężając w finale z drużyną Francji 3:1. Koszulka została zaprojektowana w kolorze białym z czerwonymi elementami. Na przodzie koszulki znajduje się logotyp sponsora polskich siatkarzy. Produkt posiada wbudowany system CLIMA gwarantujący prawidłowe odprowadzanie wilgoci na zewnątrz. Dodatkowo rękawy i boki koszulki stworzone są ze specjalnego materiału zwiększającego przewiewność.', 99.99, 'Koszulka', 'https://blob.sxv.pl/shops/media/f1000/2009/adidas/1179/0-koszulka-adidas-polo-m-e86465.jpg');
