INSERT INTO clothes(name, gender, brand, description, prize, type, image) values('Koszulka adidas reprezentacji Polski', 'Damskie', 'Adidas', 'Oficjalna meczowa koszulka reprezentacji polskich siatkarek należąca do aktualnej kolekcji kadrowej. Koszulka została wykonana w białym kolorze z czerwonymi elementami. Jej wyjątkowy charakter podkreśla zamieszczona na lewej piersi flaga Polski. Koszulka jest niezwykle dopasowana, wykonana z materiału gwarantującego wentylację na najwyższym poziomie. Panele boczne, pokryte specjalną siateczką przepuszczającą powietrze, zwiększają komfort podczas wysiłku. Warto podkreślić, iż jako jedyni w Polsce mamy możliwość nanoszenia na koszulki najnowszego wzoru numerów z logotypem "Polskiej Siatkówki" w postaci Orła. Prawdziwa gratka dla największych fanek biało-czerwonych.', 179.99, 'Koszulka', 'https://blob.sxv.pl/shops/media/f1000/2017/adidas/89540/koszulka-adidas-pzps-biaa-s98570-b-59e5f84d24696.jpg');
INSERT INTO clothes(name, gender, brand, description, prize, type, image) values('Koszulka adidas Mivball Cap J', 'Damskie', 'Adidas', 'Oficjalna koszulka meczowa reprezentacji polskich siatkarek. W tym modelu polskie zawodniczki występowały w Mistrzostwach Europy rozgrywanych w Belgii i Holandii w 2015 r. Prawdziwa gratka dla sympatyków i kibiców polskiej siatkówki. Koszulka wyposażona w system climacool, który zapewnia doskonałą wentylację podczas użytkowania. Produkt wykonany w 100% z włókien poliestrowych.', 159.99, 'Koszulka', 'https://blob.sxv.pl/shops/media/f1000/2014/adidas/45219/0-koszulka-adidas-mivball-cap-j-f82171-czerwona.jpg');
INSERT INTO clothes(name, gender, brand, description, prize, type, image) values('Koszulka adidas ESS 3S Tee', 'Damskie', 'Adidas', 'Koszulka adidas ESS 3S Tee to koszulka treningowa wchodząca w skład kolekcji adidas reprezentacji polskich siatkarek. Koszulka została zaprojektowana w kolorze szarym z białymi oraz czerwonymi elementami. Wzbogacona została o widoczną flagę polski wraz z logotypami sponsorów kadry Polski. Koszulka wykonana została z najwyższej jakości materiałów tekstylnych oraz syntetycznych. Produkt przeznaczony jest do treningu oraz ogólnej aktywności fizycznej. Może posłużyć również jako koszulka, w której będziesz wspierał polskich siatkarzy.', 9.99, 'Koszulka', 'https://blob.sxv.pl/shops/media/f1000/2013/adidas/26152/0-koszulka-adidas-x20824.jpg');
