INSERT INTO accessories(name, gender, brand, description, prize, type, image) values('Asics Gel-Kneepad', 'Dowolne', 'Asics', 'Nakolanniki produkcji asics stanowią świetne zabezpieczenie stawów kolanowych. Ich zadaniem jest ochrona kolan przed niechcianymi stłuczeniami, siniakami. Nakolanniki pokryte są białym kolorem. Posiadają mały logotyp firmy umieszczony na przedniej części ochraniaczy. Model został wzbogacony o wkładki Gel, które dodatkowo absorbują wstrząsy powstałe podczas kontaktu kolan z parkietem. ', 59.99, 'Nakolanniki', 'https://blob.sxv.pl/shops/media/f1000/2017/asics/89413/asics-gel-kneepad-146815-0001-5d7f6ded1f494.jpg');
INSERT INTO accessories(name, gender, brand, description, prize, type, image) values('Rękawki siatkarskie Mizuno męskie', 'Męskie', 'Mizuno', 'Męskie rękawki Mizuno zaprojektowane w kolorze białym z czarnym logotypem japońskiego producenta sprzętu sportowego. Rękawki zostały wykonane z elastycznego materiału, który dopasowuje się do kształtu ramion. Zadaniem rękawków jest utrzymanie temperatury ciała zapewniając jednocześnie najwyższy komfort podczas gry.', 39.99, 'Rękawki siatkarskie', 'https://blob.sxv.pl/shops/media/f1000/2018/mizuno/105291/rekawki-siatkarskie-mizuno-aw18-32ey655301-5ba241ec34949.jpg');
